    <form className='image' onSubmit={submit}>
        <div className='container col-6 p-5'>
        <div className='form-group'>
            <label>Name</label>
            <input type="text" className="form-control" value={user.name} name='name' onChange={change}/>
        </div>
        <div className='form-group'>
            <label>Description</label>
            <input type="text" className="form-control" value={user.description} name='description' onChange={change}/>
        </div>
        <div className='form-group'>
            <label>Activity Type</label>
            <div>
            <select className="custom-select" id="inputGroupSelect04" value={user.activitytype} name='activitytype' onChange={change}>
                <option selected>Choose...</option>
                <option value="run">Run</option>
                <option value="bicycle">Bicycle Ride</option>
                <option value="swim">Swim</option>
                <option value="walk">Walk</option>
                <option value="hike">Hike</option>
            </select>
            </div>
        </div>
        <div className='form-group'>
            <label>Duration</label>
            <input type="time" className="form-control" value={user.duration} name='duration' onChange={change}/>
        </div>
        <div className='form-group'>
            <label>Date</label>
            <input type="date" className="form-control" value={user.date} name='date' onChange={change}/>
        </div>
        <div className='form-group'>
            <input type="submit" className='btn btn-success'/>
        </div>
    </div>
    </form>
  )
}

export default Login
